public class Execicio1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int c = -30; c <= 45; c = c + 5) {
			System.out.println("temperatura" + " " + c + "º" + "C");
		}
		int i = -35;
		while (i < 45) {
			i = i + 5;
			System.out.println("temperatura" + " " + i + "ºC");
		}
	}
}
