public class Exercicio7 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 100; i = i + 1) {
			if (i % 3 == 0) {
				System.out.println(i);
			}
		}
		int a = 1;
		while (a <= 100) {
			if (a % 3 == 0) {
				System.out.println(a);
			}			
			a++;
		}
	}

}
